<script>
export default {
  data () {
    return {
      swiperOption: {
        pagination: {
          el: '.swiper-pagination',
          loop: true,
          delay: 3000
        }
      },
      swiperList: [],
      groupList: []
    }
  },
  created () {
    this.getSwiperList()
    this.getGroupList()
  },
  methods: {
    // 轮播图
    async getSwiperList () {
      let { body, status } = await this.$axios.get('/home/swiper')
      if (status === 200) {
        this.swiperList = body
      }
    },
    // 租房小组
    async getGroupList () {
      let { body, status } = await this.$axios.get('/home/groups')
      if (status === 200) {
        this.groupList = body
      }
    }
  }
}
</script>

